import { useState } from 'react'

const Button = ({functn, text}) => {
  return (
    <button functn>text</button>
  )
}

const StatisticLine = ({text, value, add}) => {
  return (
    <>
    {text} {value} {add} <br  />
    </>
  )
}

const Statistics = ({good, neutral, bad}) => {
  if (good === 0 && neutral === 0 && bad === 0) {
    return (
      <>
      <h1>statistics</h1>
      <p>no feedback given</p>
      </>
    )
  }

  return(
    <>
    <h1>statistics</h1>
    <StatisticLine text='good' value={good} add=''/>
    <StatisticLine text='neutral' value={neutral} add=''/>
    <StatisticLine text='bad' value={bad} add=''/>
    <StatisticLine text='all' value={good+neutral+bad} add=''/>
    <StatisticLine text='average' value={(good-bad)/(good+bad+neutral)} add=''/>
    <StatisticLine text='positive' value={(good * 100)/(good+bad+neutral)} add='%'/>
    </>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const handleGoodClick = () => {setGood(good + 1)}
  const handleNeutralClick = () => {setNeutral(neutral+ 1)}
  const handleBadClick = () => {setBad(bad + 1)}

  return (
    <div>
      <h1>give feedback</h1>
      <button onClick={handleGoodClick}>good</button><button onClick={handleNeutralClick}>neutral</button><button onClick={handleBadClick}>bad</button>
      <Statistics good={good} neutral={neutral} bad={bad}/>
    </div>
  )
}

export default App
