import { useState} from 'react'

const Countries = ({countries, newSearch}) => {

    const Country = ({country, countries}) => {
        const Index = countries.find(c => c.name.common === country)
        return(
            <>
            <h2>{country}</h2>
            <p>population {countries}</p>
            </>
        )

    }

    const countriesToShow = newSearch === ''
  ? countries
  : countries.filter(country => country.name.common.toLowerCase().includes(newSearch.toLowerCase()))

    if (countriesToShow.length <= 10 && countriesToShow.length > 1) {
        return(
            <>
            {countriesToShow.map(c => <li key={c.name.common}>{c.name.common} <button>show</button></li> )}
            </>
        )
    }

    if (newSearch.length === 0) {
        return(
            <>
            Please introduce a character in the search bar
            </>
        )
    }

    if (countriesToShow.length >= 10) {
        return(
            <>
            Too many matches, specify another filter
            </>
        )
    }

    if (countriesToShow.length == 1) {
        const choiceCountry = countriesToShow.map(c => <h2 key={c.name.common}>{c.name.common}</h2>)
        return(
            <>
            <Country country={choiceCountry} countries={countries} />
            </>
        )
    }

  }

  export default Countries
