const Course = ({course}) => {
  return (
    <>
    <Header name={course.name}/>
    <Contents course={course}/>
    <Total course={course}/>
    </>
  )
}

const Header = ({name}) => {
  return (
    <h1>{name}</h1>
  )
}


const Part = ({part, exercise}) => {
  return (
    <>
    <li>{part} {exercise}</li>
    </>
  )
}

const Contents = ({course}) => {
  return (
    <>
    <ul>
      {course.parts.map(cou => <Part key={cou.id} part={cou.name} exercises={cou.exercises}/>)}
    </ul>
    </>
  )
}

const Total = ({course}) => {
  const totalexercises = course.parts.reduce(function (previous, key) {
    return previous + course.parts[key].id;
}, 0)
  return(
    <>
    <em>total of {totalexercises} exercises</em>
    </>
  )
}

const App = () => {
  const course = {
    id: 1,
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      }
    ]
  }

  return (
    <div>
    <Course course={course}/>
    </div>
  )
}

export default App
