import { useState, useEffect } from 'react'
import axios from 'axios'
import Filter from './components/Filter'
import Persons from './components/Persons'
import PersonForm from './components/PersonForm'
import personService from './services/persons'
import './index.css'

const App = () => {
  const [persons, setPersons] = useState([])

  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newSearch, setNewSearch] = useState('')
  const [successMessage, setSuccessMessage] = useState(null)
  const [showComponent, setShowComponent] = useState(true)

  useEffect(() => {
    console.log('effect')
    personService.getAll()
    .then(initialPersons => {
      setPersons(initialPersons)
    })

  }, [])
  console.log('render', persons.length, 'persons')

  const Notification = ({ message }) => {
    if (message === null) {
      return null
    }

    return (
      <div className='success'>
        {message}
      </div>
    )
  }

  const addPerson = (event) => {
    event.preventDefault()
    const personObject = {
      name: newName,
      number: newNumber
    }
    if (persons.some(person => person.name === newName)) {
      if (window.confirm(String(newName) + ' is already added to phonebook, replace the old number with a new one?')) {
        const personToUpdate = persons.find(person => person.name === newName)
        const updatedPerson = {name: newName, number: newNumber, id: personToUpdate.id }
        personService.update(personToUpdate.id, updatedPerson)
        .then((personToUpdate) => {
          setPersons(persons.map(person => person.id === personToUpdate.id ? personToUpdate : person))
          setNewName('')
          setNewNumber('')
          setSuccessMessage('Changed ' + String(newName) + ' phone number')
          setTimeout(() => {
            setShowComponent(false);
          }, 3000)
        })
        .catch(error => {
          alert(
            `It was not posible to update  '${newName}' phone number`)
        })
      }
    } else {
      personService.create(personObject)
      .then(personObject => {
        setPersons(persons.concat(personObject))
        setNewName('')
        setNewNumber('')
        setSuccessMessage('Added ' + String(newName))

      })
    }
  }

  const deletePerson = ({person}) => {
    if (window.confirm("Delete " + person.name + "?")) {
      personService.remove(person.id)
      .then(() => {
        setPersons(persons.filter((p) => p.id !== person.id))
      })
    }
  }

  const handleSearch = (event) => {
    console.log(event.target.value)
    setNewSearch(event.target.value)
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const personsToShow = newSearch === ''
  ? persons
  : persons.filter(person => person.name.toLowerCase().includes(newSearch.toLowerCase()))

  return (
    <div>
      <h2>Phonebook</h2>
      {showComponent && <Notification message={successMessage} />}
      <Filter newSearch={newSearch} handleSearch={handleSearch}/>
      <h3>add a new</h3>
      <PersonForm addPerson={addPerson} newName={newName} newNumber={newNumber} handleNameChange={handleNameChange} handleNumberChange={handleNumberChange}/>
      <h3>Numbers</h3>
      <Persons personsToShow={personsToShow} deletePerson={deletePerson}/>
    </div>
  )
}

export default App
