import axios from 'axios'
import countryService from './services/countries'
import { useState, useEffect } from 'react'
import Finder from './components/Finder'
import Countries from './components/Countries'

function App() {
  const [countries, setCountries] = useState([])
  const [newSearch, setNewSearch] = useState('')
  useEffect(() => {
    countryService
    .getAll()
    .then(initialCountries => {
      setCountries(initialCountries)
    })
  }, [])

  console.log('render', countries.length, 'countries')

  const handleSearch = (event) => {
    console.log(event.target.value)
    setNewSearch(event.target.value)
  }

  const showCountry = () => {

  }

  const countriesToShow = newSearch === ''
  ? countries
  : countries.filter(country => country.name.common.toLowerCase().includes(newSearch.toLowerCase()))

  return (
    <div>
      <Finder newSearch={newSearch} handleSearch={handleSearch}/>
      {countriesToShow.map(c => c.name.common)}
      <Countries countries={countries} countriesToShow={countriesToShow} showCountry={showCountry}/>
    </div>
  );
}

export default App;
