const Countries = ({countries, countriesToShow, showCountry}) => {
    if (countriesToShow.length == countries.length || countriesToShow.length == 0) {
        return (
            <>
            No country matched, check the name again.
            </>
        )
    }
    if (countriesToShow.length > 10) {
        return (
            <>
            Too many matches, specify another filter
            </>
        )
    }
    else {
        return(
            <>
            {countries.map(c => <li>c.name.common <button onClick={showCountry}></button></li> )}
            </>
        )
    }

  }

  export default Countries
